from functools import reduce


def reducer(acc, command):
    floor, basement, count = acc
    floor += {"(": 1, ")": -1}[command]
    if floor == -1 and basement is None:
        basement = count
    return floor, basement, count + 1


print(reduce(reducer, [command for command in input_text], (0, None, 1)))
